// WordToGuess.js
import React from 'react';

const WordToGuess = ({ gameOver, guessedWord, answer }) => {
    return (
        <div className="text-center">
            <p>Guess the Programming Language:</p>
            {/* Check if guessedWord is a function before calling it */}
            {typeof guessedWord === 'function' ? guessedWord() : guessedWord}
            {/* Use answer directly if the game is over */}
            {gameOver ? answer : null}
        </div>
    );
};

export default WordToGuess;
