// Letter.js
import React from 'react';

const Letter = ({ onGuess }) => {
  const handleInput = (e) => {
    const letter = e.target.value.toLowerCase();
    if (/^[a-z]$/.test(letter)) {
      onGuess(letter);
    }
  };

  return (
    <input
      type="text"
      maxLength="1"
      pattern="[a-z]"
      onChange={handleInput}
      placeholder="Enter a letter"
    />
  );
};

export default Letter;
