// Import the React library
import React from 'react';

// Import the Hangman component and other components
import Hangman from './components/Hangman';
import Keyboard from './components/Keyboard';
import ResetButton from './components/ResetButton';
import HangmanImage from './components/HangmanImage';
import WordToGuess from './components/WordToGuess';
import HelpModal from './components/HelpModal';

// Functional component named App
function App() {
  // Return JSX representing the structure of the App component
  return (
    // A div with className "App" containing the Hangman component and other components
    <div className="App">
      {/* Render the Hangman component */}
      <Hangman />
      {/* Optionally, you can include other components directly in App.js */}
      <Keyboard />
      <ResetButton />
      <HangmanImage />
      <WordToGuess />
      <HelpModal />
    </div>
  );
}

// Export the App component as the default export of this file
export default App;
